#include <iostream>using namespace std;int main() {    int num1, num2, num3;    cout << "Enter First Number : ";    cin >> num1;    cout << endl << "Enter Second Number : ";    cin >> num2;    cout << endl << "Enter Third Number : ";    cin >> num3;    if( num1 > num2 && num1 > num3 ) {        cout << endl << "Largest number is " << num1 << endl;    } else if( num2 > num1 && num2 > num3 ) {        cout << endl << "Largest number is " << num2 << endl;    } else if( num3 > num1 && num3 > num2 ) {        cout << endl << "Largest number is " << num3 << endl;    } else {        cout << endl << "...All numbers are equal..." << endl;    }    return 0;}