#include <iostream>using namespace std;int main() {    cout << "This is a C++ program that performs basic arithmetic operations (addition, subtraction, multiplication, division) on two numbers provided by the user." << endl << endl;    float num1, num2;    char choice;    cout << "Enter First Number: ";    cin >> num1;    cout << endl << "Enter Second Number: ";    cin >> num2;    cout << endl << "Enter your choice (+, -, *, /): ";    cin >> choice;    switch(choice) {    case '+':        cout << endl << "Addition of " << num1 << " + " << num2;        cout << " = " << num1 + num2 << endl;        break;    case '-':        cout << endl << "Subtraction of " << num1 << " - " << num2;        cout << " = " << num1 - num2 << endl;        break;    case '*':        cout << endl << "Multiplication of " << num1 << " * " << num2;        cout << " = " << num1 * num2 << endl;        break;    case '/':        if (num2 != 0) {            cout << endl << "Division of " << num1 << " / " << num2;            cout << " = " << num1 / num2 << endl;        } else {            cout << endl << "Error : Division by zero." << endl;        }        break;    default:        cout << endl << "Invalid choice." << endl;    }    return 0;}